import { useState } from "react";
import BlockchainService from "./services/BlockchainService";
import "./App.css";

const { getTransaction, checkFraudRisk } = BlockchainService;

function App() {
  const [txHash, setTxHash] = useState("");
  const [transaction, setTransaction] = useState(null);
  const [fraudRisk, setFraudRisk] = useState(0);
  const [fraudFlags, setFraudFlags] = useState({});
  const [error, setError] = useState("");

  const handleSearch = async () => {
    try {
      setError("");
      setFraudRisk(0);
      setFraudFlags({});
      setTransaction(null);

      const txData = await getTransaction(txHash.trim());
      if (!txData || !txData.hash) {
        throw new Error("Transaction not found. Please check the hash.");
      }
      setTransaction(txData);

      const fraudResult = await checkFraudRisk(txData);
      setFraudRisk(fraudResult?.ruleBasedRisk || 0);
      setFraudFlags(fraudResult?.flags || {});
    } catch (err) {
      setError(err.message || "An error occurred while fetching the transaction.");
      setTransaction(null);
      setFraudRisk(0);
      setFraudFlags({});
    }
  };

  return (
    <div className="page-wrapper">
      {/* Floating Header */}
      <header className="floating-header">
        <div className="header-left">🔗 Logo</div>
        <div className="header-right">Fraud Analyzer</div>
      </header>

      {/* Hero Image Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Blockchain Fraud Detection System</h1>
          <p>Empowering Security. Detecting Anomalies.</p>
        </div>
      </section>

      {/* Main Card Section */}
      <main className="content-card">
        <div className="search-box">
          <input
            type="text"
            value={txHash}
            onChange={(e) => setTxHash(e.target.value)}
            placeholder="Paste Sepolia transaction hash (0x...)"
            className="hash-input"
          />
          <button onClick={handleSearch} className="analyze-btn">
            Analyze
          </button>
        </div>

        {error && <div className="error">{error}</div>}

        {transaction && (
          <div className="tx-details">
            <h2>Transaction Details</h2>
            <div className="tx-info">
              <div><span>Hash:</span> {transaction.hash}</div>
              <div><span>From:</span> {transaction.from}</div>
              <div><span>To:</span> {transaction.to || "Contract Creation"}</div>
              <div><span>Value:</span> {transaction.value} ETH</div>
              <div><span>Gas Price:</span> {transaction.gasPrice} Gwei</div>
              <div><span>Age:</span> {transaction.ageHours} hours</div>
            </div>
          </div>
        )}

        {transaction && (
          <div className="fraud-risk-section">
            <h2>🚨 Fraud Analysis</h2>
            <div className={`risk-indicator ${fraudRisk > 50 ? "high-risk" : "low-risk"}`}>
              Fraud Risk: <strong>{fraudRisk}%</strong>
            </div>

            {Object.keys(fraudFlags).length > 0 && (
              <div className="fraud-flags">
                <h3>⚠️ Detected Fraud Flags:</h3>
                <ul>
                  {Object.keys(fraudFlags).map((flag) => (
                    <li key={flag}>{flag}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>© 2025 Blockchain Fraud Detection Team | MIT License</p>
        <p>Contact: fraud@blockchain.org</p>
      </footer>
    </div>
  );
}

export default App;
